<?phpnamespace AppBundle\Command;use Symfony\Bundle\FrameworkBundle\Command\ContainerAwareCommand;use Symfony\Component\Console\Input\InputInterface;use Symfony\Component\Console\Output\OutputInterface;use GraphAware\Neo4j\Client\ClientBuilder;class FixturesCommand extends ContainerAwareCommand{    protected function configure()    {        $this            ->setName('test:fixtures')            ->setDescription('Load fixtures');    }    protected function execute(InputInterface $input, OutputInterface $output)    {        $client = ClientBuilder::create()            ->addConnection('default', 'http://neo4j:parolaneo4j@46.101.106.91:7474')->build();        $query = $this->getRunQuery();        $params = $this->getArrayParameters();        foreach ($query as $key => $value) {            $client->run($value, $params[$key]);        }        return true;    }    protected function getRunQuery()    {        $query = [            1 => "CREATE (n:TestI) SET n += {labels}",            2 => "CREATE (n:TestStuff) SET n += {labels}"        ];        return $query;    }    protected function getArrayParameters()    {        $params[1] = [            1 => ['labels' => ['name' => 'Alex', 'age' => 35]],            2 => ['labels' => ['name' => 'Adi', 'age' => 21]]        ];        $params[2] = [            1 => ['labels' => ['work' => 'Hackathon', 'distractie' => 'Stand Up']],        ];        $params[2] = [            1 => ['labels' => ['work' => 'Hackathon', 'distractie' => 'Stand Up']],        ];        return $params;    }}